        openedWindows: openedWindows,

        addBackdrop: function () {
            buildBackdrop();
        },

        changeBackdrop: function (topModalInstance) {
            changeBackdrop(topModalInstance);
        },

        removeBackdrop: function () {
            checkRemoveBackdrop();
        },

